const accordion=document.getElementsByClassName("contentBx");for(i=0;i<accordion.length;i++)accordion[i].addEventListener("click",(function(){this.classList.toggle("active");const e=this.querySelector(".note__text").offsetHeight,t=this.querySelector(".content");this.classList.contains("active")?(t.style.height=`${e+10}px`,t.style.marginTop="20px"):(t.style.height="0px",t.style.marginTop="0px")}));const formHeight=document.querySelector(".main__form").offsetHeight;document.querySelector(".form__item").style.height=`${formHeight+60}px`;const form=document.getElementById("form");async function sendForm(e){e.preventDefault();let t=validateErrors(form),o=new FormData(form);if(0===t){let e=await fetch("sendmail.php",{method:"POST",body:o});if(e.ok){await e.json();form.reset()}}else console.log(t)}function validateErrors(e){let t=0;return document.querySelectorAll(".mb-3 > input").forEach(((e,o)=>{""===e.value&&t++})),t}function cardScroll(){document.addEventListener("scroll",(e=>{const t=document.querySelector(".main__header").getBoundingClientRect().top;document.querySelector(".main__header").offsetHeight;document.querySelector(".header__card-list").style.transform=`translateX(${t}px)`}))}form.addEventListener("submit",sendForm),cardScroll();